mapper.select:
select
  comment_id, user_id, comment_content, comment_mess_id, comment_user_id, comment_file
from test_database.comment_mess


        and comment_id = #{commentId}


        and user_id = #{userId}


        and comment_content = #{commentContent}


        and comment_mess_id = #{commentMessId}


        and comment_user_id = #{commentUserId}


        and comment_file = #{commentFileByte}
